import React, { useState, useContext } from 'react';
import {  Link, useNavigate } from 'react-router-dom';
import axios from 'axios'
import { UserContext } from './UserContext';

const Loureq = () => {
  const navigate = useNavigate();
  const [confirm, setConfirm] = useState(null);
  const [signupDetails, setSignupDetails] = useState({ 
    name1: "",
    dob1: "",
    gender1: "",
    aadharno: "",
    contact: "",
    nation: "",
    state: "",
    address: "",
    accno: "",
    occup: "",

   });
   
   const { setUser } = useContext(UserContext); 

  const checkUserExists = async (username) => {
      const response = await axios.get('https://65643730ceac41c0761da0dd.mockapi.io/api/v1/users');
      return response.data.some(user => user.username === username);
  };

  const handleSignup = async (e) => {
    e.preventDefault();
    const userExists = await checkUserExists(signupDetails.username);
    if (userExists) {
      console.log('User already exists. Redirecting to login...');
      navigate('/');
      return;
    }

    if (confirm !== signupDetails.password){
      alert('Password and Confirm Password are not same...')
      return;
    }

    axios.post('', signupDetails)
      .then(response => {
        console.log('Signup successful:', response.data);
        setUser(response.data);
        navigate('/');
      })
      .catch(error => {
        console.error('Signup error:', error);
      });
  };

  const handleInputChange = (e) => {
    // alert(e.target.name);
    setSignupDetails({ ...signupDetails, [e.target.name]: e.target.value });
  };

  const handleConfirmPassword = (e) => {
    setConfirm(e.target.value);
  };

  return (
    <div className='whole'>
    <div style={{ maxWidth: '400px', margin: 'auto' }}>
    <div >
      <form onSubmit={handleSignup} className='reg'>
      <br></br>
      <h2 className='m'>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;LOU REQUEST</h2>

        <table>
          <tbody>
            <tr>
              <td className='z'><b>NAME :</b></td>
              <td>
                <input
                  type="text"
                  name="username"
                  value={signupDetails.username}
                  onChange={handleInputChange}
                  required
                />
              </td>
            </tr>
            <tr>
              <td className='z'><b>DOB :</b></td>
              <td>
                <input
                  type="dob"
                  name="dob"
                  value={signupDetails.dob}
                  onChange={handleInputChange}
                  required
                />
              </td>
            </tr>

            <tr>
              <td className='z'><b>GENDER :</b></td>
              <td>
                  <select name="gender" onChange={handleInputChange}>
                    <option></option>
                    <option value="MALE">MALE</option>
                    <option value="FEMALE">FEMALE</option>
                    <option value="OTHERS">OTHERS</option>
                  </select>
              </td>
            </tr>

            <tr>
              <td className='z'><b>AADHAR NUMBER :</b></td>
              <td>
                <input
                    type="number"
                    name="aadharno"
                  value={signupDetails.aadharno}
                  onChange={handleInputChange}
                    required
                />
              </td>
            </tr>

            <tr>
              <td className='z'><b>ACCOUNT NUMBER :</b></td>
              <td>
                <input
                    type="number"
                    name="accno"
                  value={signupDetails.accno}
                  onChange={handleInputChange}
                    required
                />
              </td>
            </tr>

            <tr>
              <td className='z'><b>MOBILE NUMBER :</b></td>
              <td>
                <input
                  type="tel"
                  name="contact"
                  value={signupDetails.contact}
                  onChange={handleInputChange}
                  pattern="[0-9]{10}"
                  required
                />
              </td>
            </tr>

            <tr>
              <td className='z'><b>EMAIL :</b></td>
              <td>
                <input
                  type="email"
                  name="email"
                  value={signupDetails.email}
                  onChange={handleInputChange}
                  required
                />
              </td>
            </tr>
            
            <tr>
              <td className='z'><b>NATIONALITY :</b></td>
              <td>
                <input
                    type="text"
                    name="nation"
                  value={signupDetails.nation}
                  onChange={handleInputChange}
                    required
                />
              </td>
            </tr>
            <tr>
              <td className='z'><b>STATE:</b></td>
              <td>
                <input
                    type="text"
                    name="state"
                  value={signupDetails.state}
                  onChange={handleInputChange}
                    required
                />
              </td>
            </tr>
            <tr>
              <td className='z'><b>ADDRESS :</b></td>
              <td>
                <input
                    type="text"
                    name="address"
                  value={signupDetails.address}
                  onChange={handleInputChange}
                    required
                />
              </td>
            </tr>

   
            <tr>
                <td colSpan="2" style={{ textAlign: 'center' }}>
                  <Link to="">
                  <button type="submit">Register</button>
                  </Link>
                </td>
            </tr>
          </tbody>
        </table>
      </form>
    </div>
    </div>
    </div>
  );
};

export default Loureq;
